import React, { useState } from "react";
import Card from "../UI/Card";
import ExpenseItem from "./ExpenseItem";
import "./Expenses.css";
import ExpensesFilter from "./ExpensesFilter";

function Expenses(props) {
  let [filteredYear, setFilteredYear] = useState("2020");

  const changeFilterHandler = (selectedYear) => {
    setFilteredYear(selectedYear);
    console.log(selectedYear)
  };

  const filteredExpenses = props.item.filter(expense => {
    return expense.date.getFullYear().toString() === filteredYear;
  })


  return (
    <Card className="expenses">
      <ExpensesFilter
        onChangeFilter={changeFilterHandler}
        selected={filteredYear}
      />
      {filteredExpenses.length ===0 ? <p>No expenses found</p> : filteredExpenses.map(filterExpense => (
         <ExpenseItem
         key={filterExpense.id}
         title={filterExpense.title}
         amount={filterExpense.amount}
         date={filterExpense.date}
       />
      ))}
      {}
      {/* {props.item.map((expense) => (
        <ExpenseItem
          key={expense.id}
          title={expense.title}
          amount={expense.amount}
          date={expense.date}
        />
      ))} */}
    </Card>
  );
}

export default Expenses;
